package com.sefa.timer;import com.sefa.events.RandomsGenerated;import com.sefa.random.RandomLongProvider;import com.sefa.random.RandomStringProvider;import com.sefa.receiver.TimedMessageReceiver;import org.apache.log4j.Logger;import java.util.TimerTask;public class RandomTimerTask extends TimerTask {    private static final Logger log = Logger.getLogger(RandomTimerTask.class);    private RandomLongProvider longProvider;    private RandomStringProvider stringProvider;    private TimedMessageReceiver receiver;    private int timeToShow;    public RandomTimerTask(RandomLongProvider longProvider, RandomStringProvider stringProvider, TimedMessageReceiver receiver) {        this.longProvider = longProvider;        this.stringProvider = stringProvider;        this.receiver = receiver;    }    @Override    public void run() {        log.info("generating randoms");        receiver.receiveTimedMessage(generateRandoms());    }    private RandomsGenerated generateRandoms() {        return new RandomsGenerated(longProvider.nextRandomLong(), stringProvider.nextRandomString(), timeToShow);    }    public void setTimeToShow(int timeToShow) {        this.timeToShow = timeToShow;    }}